def parse_duration(time_string:str)->int:
    """
    Convert time strings into durations in seconds.

    Args:
        time_string: A string representing a duration (e.g., "1y 15d 5h", "3 hours")

    Returns:
        int: Total duration in seconds

    Examples:
        "1y 15d 5h"
        32832000
        "5d 3h"
        442800
        parse_duration("3 hours")
        10800
        parse_duration("3 seconds")
        3
        parse_duration("2 hours, 1 seconds")
        7201
    """
    # Define time unit conversions to seconds
    units = {
        'y': 365 * 24 * 60 * 60,  # years
        'years': 365 * 24 * 60 * 60,  # years
        'd': 24 * 60 * 60,  # days
        'days': 24 * 60 * 60,  # days
        'h': 60 * 60,  # hours
        'hours': 60 * 60,  # hours
        'm': 60,  # minutes
        'minutes': 60,  # minutes
        's': 1,  # seconds
        'seconds': 1  # seconds
    }

    # Normalize the string: remove commas, convert to lowercase
    time_string = time_string.lower().replace(',', '')

    # Pattern to match numbers followed by time units
    # Matches: "1y", "15d", "5h", "3 hours", "1 second", etc.
    pattern = r'(\d+)\s*([a-z]+)'
    matches = re.findall(pattern, time_string)

    total_seconds = 0

    for value, unit in matches:
        value = int(value)

        # Get the first character of the unit to match our units dict
        unit_key = unit[0].lower()

        if unit_key in units:
            total_seconds += value * units[unit_key]

    return total_seconds


