def scrape_magma_node_page(url:str)->Optional[LightningNode]:
    """Scrape data from a single node page"""


    try:
        # Extract node address from URL
        node_address = url.split('/node/')[1]

        if DEV_MODE and node_address in DEV_MODE_NODE_PAGES:
            html=DEV_MODE_NODE_PAGES[node_address]
        else:
            #response = requests.get(url, headers=headers, timeout=30)
            #response.raise_for_status()
            from selenium.webdriver.firefox.options import Options
            options = Options()
            options.add_argument("--headless")
            driver = webdriver.Firefox(options=options)

            driver.get(url)
            time.sleep(3)
            driver.execute_script("window.scrollTo(0, document.body.scrollHeight);")
            time.sleep(10)
            html = driver.page_source
            driver.close()
        soup = BeautifulSoup(html, 'html.parser')


        # Initialize default values
        min_channel_size:int = None
        country:int = None
        oldest_channel:Optional[datetime] = None
        total_capacity:int = None
        last_update_magma:datetime = None
        smallest_channel:int=None
        smallest_channel_size:int=None
        number_of_channels:int=None
        tor_address:Optional[str]=None
        ipv4_address:Optional[str]=None
        ipv6_address:Optional[str]=None # not actually searched for yet, finding a proper regex for this is a nightmare

        # Try to find capacity (look for numbers followed by "sats" or "BTC")
        div = soup.find(string='Total Capacity')
        if not div:
            print(f'Error, no total capacity div found for node : {node_address}')
        else:
            div=div.parent.parent.parent
            text=div.get_text()
            capacity_match = re.search(r'([\d,]+)\s*sats?', text, re.IGNORECASE)
            capacity_dash_match = re.search(r'(-)*sats?', text, re.IGNORECASE)
            if capacity_match:
                total_capacity = int(capacity_match.group(1).replace(',', ''))
                total_capacity_in_BTC = total_capacity/100000000
                if total_capacity<CAPACITY_MINIMUM:
                    print(f"Node has low capacity {total_capacity_in_BTC}BTC {node_address}")
            elif capacity_dash_match:
                print(f'Node has no listed capacity (new node), skipping : {node_address}')
            else:
                print(f'Error, no total capacity found for node, this shouldnt happen : {node_address}')

        # Try to find number of channels
        div = soup.find(string='Number of Channels')
        if not div:
            print(f'Error, no Number of channels div found for node : {node_address}')
        else:
            div = div.parent.parent.parent
            text = div.get_text()
            min_channel_match = re.search(r'([\d,]+)\s*Channels?', text, re.IGNORECASE)
            if min_channel_match:
                number_of_channels = int(min_channel_match.group(1).replace(',', ''))
            else:
                print(f'Error, no Number of channels found for node : {node_address}')

        # Find smallest channel size
        div = soup.find(string='Smallest Channel')
        if not div:
            print(f'Error, no smallest channel size div found for node : {node_address}')
        else:
            div=div.parent.parent.parent
            text = div.get_text()
            min_channel_match = re.search(r'([\d,]+)\s*sats?', text, re.IGNORECASE)
            if min_channel_match:
                smallest_channel_size = int(min_channel_match.group(1).replace(',', ''))
            else:
                print(f'Error, no smallest channel size found for node : {node_address}')

        # Try to find min channel size
        div = soup.find(string='Min Channel Size')
        if not div:
            pass
            #print(f'Warning, no min channel size div found for node (this is expected for some nodes) : {node_address}')
        else:
            div=div.parent.parent
            text = div.get_text()
            min_channel_match = re.search(r'([\d,]+)\s*sats?', text, re.IGNORECASE)
            if min_channel_match:
                min_channel_size = int(min_channel_match.group(1).replace(',', ''))
            else:
                print(f'Error, no min channel size found for node : {node_address}')

        # Try to find last update
        div = soup.find(string='Last Update')
        if not div:
            print(f'Error, no last update div found for node : {node_address}')
        div=div.parent.parent.parent.parent
        text = div.get_text()
        update_match = re.search(r'\d+.\s*second[s]*\s*ago|\d+.\s*minute[s]*\s*ago|\d+.\s*hour[s]*\s*ago', text, re.IGNORECASE)
        if update_match:
                last_update = parse_duration(update_match.string)
                last_update_magma=datetime.now()-timedelta(seconds=last_update)
                #print(f'Last update {update_match.string} produces {last_update} seconds')
        else:
            print(f'Error, no last update found for node : {node_address}')

        # Try to find oldest channel
        div = soup.find(string='Oldest Channel')
        if not div:
            print(f'Error, no oldest channel div found for node : {node_address}')
        else:
            div=div.parent.parent.parent
            text = div.get_text()
            oldest_match = re.search(r'Oldest\s*Channel[:\s]*([\d\w\s]+)', text, re.IGNORECASE)
            if oldest_match:
                oldest_channel_str = str(oldest_match.group(1).strip())
                oldest_channel_duration = parse_duration(oldest_channel_str)
                oldest_channel=datetime.now()-timedelta(seconds=oldest_channel_duration)
                #print(f'Oldest channel {oldest_channel_str} produces {oldest_channel} seconds')
            else:
                print(f'Error, no oldest channel found for node : {node_address}')
                oldest_channel=datetime.now()

        # Try to find onion address
        all_input_objects = soup.select('input')
        all_inputs = str([input_tag['value'] + ',' for input_tag in all_input_objects])
        oldest_match = re.search(r'\w+\.onion:\d{1,5}', all_inputs, re.IGNORECASE)
        if oldest_match:
            tor_address = oldest_match.group().strip()
        else:
            if 'onion' in soup.text:
                print(f'Error, no tor channel found for node : {node_address}')

        # Try to find IPv4 address
        all_input_objects = soup.select('input')
        all_inputs=str([input_tag['value']+',' for input_tag in all_input_objects])
        oldest_match = re.search(r'[0-9.]{7,15}:[0-9]{1,5}', all_inputs, re.IGNORECASE)
        if oldest_match:
            ipv4_address = str(oldest_match.group())

        if not tor_address and not ipv4_address and not ipv6_address:
            print(f'Warning: this node has no tor, IPv4, or IPv6 address address. This can happen if node ONLY has IPv6: {node_address[-6:]}')



        node = LightningNode(
            node_address=node_address.lower(),
            min_channel_size=min_channel_size,
            country=country,
            oldest_channel=oldest_channel,
            number_of_channels=number_of_channels,
            total_capacity=total_capacity,
            smallest_channel_size=smallest_channel_size,
            oldest_known_date=oldest_channel,
            tor_address=tor_address,
            ipv4_address=ipv4_address,
        )
        node.set_oldest_known_date()

        #print(f"Scraped node: {node_address[:8]}...")
        return node

    except Exception as e:
        print(f"Error scraping {url}: {e}")
        traceback.print_exc()
        return None
def update_node(existing_node:LightningNode)->None:
    """
    Scrape amboss for lightning node, update in db
    """
    print(f'Found existing node: {existing_node.node_address[-6:]}, querying again..')
    node_url=existing_node.get_amboss_url()
    new_lightning_node = scrape_magma_node_page(node_url)
    if not new_lightning_node:
        print(f"Error re-scraping node: {existing_node.node_address[-6:]}")
        return
    else:
        update_existing_lightning_node(existing_node, new_lightning_node)
        return
