def update_existing_lightning_node(existing_node: LightningNode, new_node: LightningNode)->None:
    """
    Given scraped information about new node, layer on top of old information, replacing data as needed. Will save the object.
    """
    # evaluate values based on size/age
    choose_smaller_number={}
    choose_larger_number={'magma_queries','remote_close_count','failed_uptime_checks','total_uptime_checks'}
    choose_oldest_date={'oldest_known_date','oldest_channel'}
    choose_most_recent_date={'last_channel_creation_attempt','last_seen_online'}
    # automatically pick the value from new_node
    choose_newer_value={'min_channel_size','country','number_of_channels','total_capacity','smallest_channel_size','tor_address','ipv4_address','ipv6_address','last_magma_query'}
    # automatically pick the value from existing_node
    choose_older_value={}
    for attribute_name, attribute_value in vars(new_node)['__data__'].items():
        if '__' in attribute_name or attribute_name.startswith('_'):
            continue
        if not attribute_value:
            continue
        existing_value=getattr(existing_node,attribute_name,None)
        if not existing_value:
            print(f'Found updated value: {attribute_name}:{attribute_value}')
            setattr(existing_node,attribute_name,attribute_value)
            continue
        if attribute_name in choose_newer_value:
            setattr(existing_node, attribute_name, attribute_value)
            continue
        if attribute_name in choose_older_value:
            continue
        if isinstance(attribute_value,int) or isinstance(attribute_value,float):
            if attribute_name in choose_smaller_number:
                setattr(existing_node,attribute_name,min(existing_value,attribute_value))
            elif attribute_name in choose_larger_number:
                setattr(existing_node,attribute_name,max(existing_value,attribute_value))
            else:
                print(
                    f'Warning in update_existing_lightning_node(), unhandled number : {attribute_name}{attribute_value}')
        elif isinstance(attribute_value,datetime):
            if attribute_name in choose_oldest_date:
                setattr(existing_node,attribute_name,min(existing_value,attribute_value))
            elif attribute_name in choose_most_recent_date:
                setattr(existing_node,attribute_name,max(existing_value,attribute_value))
            else:
                print(
                    f'Warning in update_existing_lightning_node(), unhandled datetime : {attribute_name}{attribute_value}')
        elif isinstance(attribute_value,str):
            setattr(existing_node,attribute_name,attribute_value)
        else:
            print(f'Warning in update_existing_lightning_node(), unhandled type : {attribute_name}{type(attribute_value)}')
    existing_node.set_oldest_known_date()
    existing_node.save()
def dump_to_json():
    with open("data.json", "w") as outfile:
        json.dump(list(LightningNode.select()), outfile, indent=4,cls=node_database.CustomEncoder)
def main(force_homepage_fetch:bool=False,skip_node_refresh:bool=False):
    """Main execution function"""
    print("Starting Amboss.space scraper...")

    # Step 1: Initialize database
    scraped_node_count = 0

    # Step 2 & 3: Get links from magma homepage
    if force_homepage_fetch:
        print("\nFetching nodes from homepage...")
        links_to_check = get_magma_homepage_links()
    else:
        links_to_check = {}

    # Get nodes which are due for a refresh, append them
    if not skip_node_refresh:
        cutoff_date = datetime.now() - timedelta(days=MAGMA_UPDATE_FREQUENCY)
        old_links: List[Optional[LightningNode]] = LightningNode.select().where(
            LightningNode.last_magma_query < cutoff_date)
        for found_node in old_links:
            if found_node.node_address not in links_to_check:
                links_to_check[found_node.node_address] = f"https://amboss.space/node/{found_node.node_address}"


    for node_id,node_url in links_to_check.items():
            scraped_node=None
            if scraped_node_count>=MAGMA_MAX_NODE_QUERIES_PER_RUN:
                print('stopping scraping bc MAGMA_MAX_NODE_QUERIES_PER_RUN')
                break
            existing_node:Optional[LightningNode]= LightningNode.get_or_none(LightningNode.node_address == node_id)
            if existing_node:
                if existing_node.needs_magma_update(MAGMA_UPDATE_FREQUENCY) or FORCE_MAGMA_NODE_UPDATES:
                    scraped_node_count += 1
                    update_node(existing_node)
                else:
                    print(f'Found existing node: {existing_node.node_address[-6:]}, not querying again bc queried recently')
                    continue
            else:
                print(f"Scraping LN node: {node_id[-6:]}")
                scraped_node = scrape_magma_node_page(node_url)
                scraped_node_count += 1
                scraped_node.save(force_insert=True)


        # Step 5: Print all nodes

    print_all_nodes()
    print("\nScraping completed!")

def load_from_json():
    with open('data.json', 'r') as file:
        loaded_json = json.load(file)
        new_nodes=[]
        for found_dict in loaded_json:
            try:
                temp_node=LightningNode.from_json(found_dict)
                existing_node=LightningNode.get_or_none(LightningNode.node_address==temp_node.node_address)
                if existing_node:
                    update_existing_lightning_node(existing_node,temp_node)
                else:
                    temp_node.save(force_insert=True)
                    new_nodes.append(temp_node)
            except Exception as e:
                print(f"Error importing temp_node in load_from_json: {e}:{found_dict}")
                continue
        for idx in range(0, len(new_nodes), 100):
            LightningNode.insert_many(new_nodes[idx:idx + 100]).execute()
if __name__ == "__main__":
    main(force_homepage_fetch=True)
    dump_to_json()